<?phpclass Attribute_Map{		private $db_type;	private $app_type;	private $table_name;		private $rel_type;	private $rel_with;	private $column_name;	private $db_constraint;	public function __construct($app_t, $db_t, $rel_t, $rel_w, $column_name, $table_name)	{		$this->db_type = $db_t;		$this->app_type = $app_t;		$this->rel_type = $rel_t;		$this->rel_with = $rel_w;		$this->column_name = $column_name;		$this->table_name = $table_name;	}	public function set_db_constraint($cons)	{		$this->db_constraint = $cons;	}		public function as_array()	{		$res = get_object_vars($this);		if (empty($this->db_constraint))			unset($res['db_constraint']);		return $res;	}}